import React, { Component } from 'react';
import flv from 'flv.js';
import { connect } from 'react-redux';
import { fetchStream } from '../../actions';

class StreamShow extends Component {
  constructor(props) {
    super(props);

    this.videoRef = React.createRef();
  }

  componentDidMount(){
    const { id }  = this.props.match.params.id;
    this.props.fetchStream(id);
    // this.buildPlayer();
  }

  // componentDidUpdate() {
  //   this.buildPlayer();
  // }

  // buildPlayer() {
  //   if (this.player || !this.props.stream) {
  //     return;
  //   }
  //   const { id }  = this.props.match.params.id;
  //   this.player = flv.createPlayer({
  //     type: 'flv',
  //     url: `http://localhost:8000/live/${id}.flv`
  //   });
  //
  //   this.player.attachMediaElement(this.videoRef.current);
  //   this.player.load();
  // }

  render(){
    if (!this.props.stream){
      return <div>Loading...</div>
    }

    const { title, description } = this.props.stream; // This destructuring helps to avoid 'this.props.stream' repetition in the JSX below

    // BELOW:  writing "controls" is the same as writing controls={true}, this only applies to true not false
    return (
      <div>
        <video ref={this.videoRef} style={{ width: '100%' }} controls />
        <h1>{title}</h1>
        <h5>{description}</h5>
      </div>
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  return { stream: state.streams[ownProps.match.params.id] }
}
export default connect(mapStateToProps, { fetchStream })(StreamShow);
